mapper.delete:

delete from seckill_success
where seckill_id = #{seckillId,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}

mapper.insert:


  SELECT LAST_INSERT_ID()

insert into seckill_success (user_id, state, create_time
  )
values (#{userId,jdbcType=INTEGER}, #{state,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:

update seckill_success
set state = #{state,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where seckill_id = #{seckillId,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}

mapper.select:

select seckill_id, user_id, state, create_time
from seckill_success
where seckill_id = #{seckillId,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}
